$(function(){$("body").on("change","input.page-selector",function(){var a=$(this).data("url")+"&page="+$(this).val(),e=$(this).closest("div[data-pjax-container]").attr("id");$.pjax({url:a,container:"#"+e})}).on("change","select.page-size",function(){var a=$(this).attr("data-page");console.log("current-page: ",a);var e=$(this).attr("data-per-page");console.log("current-per-page: ",e),console.log("current-page(-1): ",a-1),console.log("current-page(-1)*current-per-page: ",(a-1)*e),console.log("new-page: ",$(this).val());var t=(a-1)*e/$(this).val();console.log("calc-page: ",t);var o=Math.ceil(t)+1;console.log("round-page(+1): ",o);var r=$(this).data("url")+"&page="+o+"&per-page="+$(this).val();console.log("url: ",r);var n=$(this).closest("div[data-pjax-container]").attr("id");$.pjax({url:r,container:"#"+n})})});