$(function(){$("body").on("change","input.page-selector",function(){var a=$(this).data("url")+"&page="+$(this).val(),t=$(this).closest("div[data-pjax-container]");t.length?$.pjax({url:a,container:"#"+t.attr("id")}):location.href=a}).on("change","select.page-size",function(){var a=($(this).attr("data-page")-1)*$(this).attr("data-per-page")/$(this).val(),t=Math.ceil(a)+1,e=$(this).data("url")+"&page="+t+"&per-page="+$(this).val(),i=$(this).closest("div[data-pjax-container]");i.length?$.pjax({url:e,container:"#"+i.attr("id")}):location.href=e})});